<script setup>
import { ref, computed, onMounted, onUnmounted } from 'vue'
import PopupMainStake from './popup/popup-main-stake.vue'
import useStake from '../../../composables/stake.js'

// –†–µ–∞–∫—Ç–∏–≤–Ω–æ–µ —Å–æ—Å—Ç–æ—è–Ω–∏–µ –¥–ª—è —É–ø—Ä–∞–≤–ª–µ–Ω–∏—è popup
const isStakePopupOpen = ref(false)

// Composable –¥–ª—è —Å—Ç–µ–π–∫–∏–Ω–≥–∞
const { 
  stakeData, 
  isLoading,
  isCollecting,
  canCollect,
  collectProfit,
  loadStakeStats,
  startProfitUpdates,
  stopProfitUpdates,
  formatAmount,
  error 
} = useStake()

// Computed properties –¥–ª—è —Ñ–æ—Ä–º–∞—Ç–∏—Ä–æ–≤–∞–Ω–∏—è
const formatBalance = computed(() => {
  return (amount) => {
    const parts = parseFloat(amount).toFixed(2).split('.')
    return `${parts[0]}<span class="text-[16px] opacity-60">.${parts[1]}</span>`
  }
})

const formatProfit = computed(() => {
  return (amount) => {
    return parseFloat(amount).toFixed(6)
  }
})

// –§—É–Ω–∫—Ü–∏—è –¥–ª—è –æ—Ç–∫—Ä—ã—Ç–∏—è popup
const openStakePopup = () => {
  isStakePopupOpen.value = true
}

// –û–±—Ä–∞–±–æ—Ç—á–∏–∫ —Å–±–æ—Ä–∞ –ø—Ä–∏–±—ã–ª–∏
const handleCollectProfit = async () => {
  if (!canCollect.value || isCollecting.value) return
  
  try {
    console.log('üè¶ –°–±–æ—Ä –ø—Ä–∏–±—ã–ª–∏ —Å–æ —Å—Ç–µ–π–∫–∏–Ω–≥–∞')
    await collectProfit()
    
    // –û–±–Ω–æ–≤–ª—è–µ–º –¥–∞–Ω–Ω—ã–µ –ø–æ—Å–ª–µ —Å–±–æ—Ä–∞
    await loadStakeStats()
    
  } catch (err) {
    console.error('‚ùå –û—à–∏–±–∫–∞ —Å–±–æ—Ä–∞ –ø—Ä–∏–±—ã–ª–∏:', err)
  }
}

// –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏—è
onMounted(async () => {
  console.log('üöÄ –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏—è –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–∞ stake.vue')
  
  try {
    await loadStakeStats()
    
    // –ó–∞–ø—É—Å–∫–∞–µ–º –∞–≤—Ç–æ–æ–±–Ω–æ–≤–ª–µ–Ω–∏–µ –µ—Å–ª–∏ –µ—Å—Ç—å –∞–∫—Ç–∏–≤–Ω—ã–π —Å—Ç–µ–π–∫
    if (stakeData.value.stakeBalance > 0) {
      startProfitUpdates()
    }
  } catch (err) {
    console.error('‚ùå –û—à–∏–±–∫–∞ –∏–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏–∏ stake.vue:', err)
  }
})

// –û—á–∏—Å—Ç–∫–∞ –ø—Ä–∏ —Ä–∞–∑–º–æ–Ω—Ç–∏—Ä–æ–≤–∞–Ω–∏–∏
